CREATE TABLE DBA_CSYT."HSBA" (
	"Ma_HSBA" INT NOT NULL,
	"Ma_Benh_Nhan" INT NOT NULL,
	"Ngay" DATE NOT NULL,
	"Chan_Doan" NVARCHAR2(255) NOT NULL,
	"Ma_BS" INT NOT NULL,
	"Ma_Khoa" INT NOT NULL,
	"Ma_CSYT" INT NOT NULL,
	"Ket_Luan" NVARCHAR2(255) NOT NULL,
	constraint HSBA_PK PRIMARY KEY ("Ma_HSBA"));

