create or replace procedure create_csyt(
    p_ten_csyt in nvarchar2,
    p_dia_chi in nvarchar2,
    p_sdt in nvarchar2
)
is
    p_id number;
    lv_stmt   VARCHAR2 (1000);
begin
    select "CSYT_MA_CSYT_SEQ".nextval into p_id from dual;
    lv_stmt := 'CREATE USER CSYT' || p_id || ' IDENTIFIED BY CSYT' || p_id;
    DBMS_OUTPUT.put_line(lv_stmt);
	EXECUTE IMMEDIATE ( lv_stmt );
    lv_stmt := 'INSERT INTO CSYT("Ma_CSYT", "Ten_CSYT", "DC_CSYT", "SDT_CSYT" ) VALUES(' || p_id || ',''' || p_ten_csyt || ''',''' || p_dia_chi || ''',''' || p_sdt || ''')';
    DBMS_OUTPUT.put_line(lv_stmt);
	EXECUTE IMMEDIATE ( lv_stmt );
end create_csyt;
/
create or replace procedure create_benh_nhan(
    p_id_csyt in number,
    p_ten_benh_nhan in nvarchar2,
    p_cmnd in nvarchar2,
    p_ngay_sinh in nvarchar2,
    p_so_nha in nvarchar2,
    p_ten_duong in nvarchar2,
    p_quan_huyen in nvarchar2,
    p_tinh_tp in nvarchar2,
    p_tieu_su_benh in nvarchar2,
    p_tieu_su_benh_gd in nvarchar2,
    p_di_ung_thuoc in nvarchar2
)
is
    p_id number;
    lv_stmt   VARCHAR2 (1000);
begin
    select "BENH_NHAN_MA_BENH_NHAN_SEQ".nextval into p_id from dual;
    lv_stmt := 'CREATE USER BN' || p_id || ' IDENTIFIED BY BN' || p_id;
	EXECUTE IMMEDIATE ( lv_stmt );
    lv_stmt := 'INSERT INTO "Benh_Nhan"("Ma_Benh_Nhan", "Ma_CSYT", "Ten_Benh_Nhan", "CMND", "Ngay_Sinh", "So_Nha", "Ten_Duong", "Quan_Huyen", "Tinh_TP", "Tieu_Su_Benh", "Tieu_Su_Benh_GD", "Di_Ung_Thuoc") 
    VALUES(' || p_id || ',' || p_id_csyt || ',''' || p_ten_benh_nhan || ''',''' || p_cmnd || ''', TO_DATE(''' || p_ngay_sinh ||''',''YYYY-MM-DD'')' || ',''' || p_so_nha
    || ''',''' || p_ten_duong || ''',''' || p_quan_huyen || ''',''' || p_tinh_tp || ''',''' || p_tieu_su_benh || ''',''' || p_tieu_su_benh_gd || ''',''' || p_di_ung_thuoc|| ''')';
	EXECUTE IMMEDIATE ( lv_stmt );
end create_benh_nhan;
/
create or replace procedure create_nhan_vien(
    p_type in nvarchar2,
    p_ho_ten in nvarchar2,
    p_phai in nvarchar2,
    p_ngay_sinh in nvarchar2,
    p_cmnd in nvarchar2,
    p_que_quan in nvarchar2,
    p_sdt in nvarchar2,
    p_csyt in number,
    p_vai_tro in nvarchar2,
    p_chuyen_khoa in nvarchar2
)
is
    p_id number;
    lv_stmt   VARCHAR2 (1000);
begin
    select "NHAN_VIEN_MA_NHAN_VIEN_SEQ".nextval into p_id from dual;
    if p_type = 'TT' then
        lv_stmt := 'CREATE USER TT' || p_id || ' IDENTIFIED BY TT' || p_id;
    elsif p_type = 'YBS' then
        lv_stmt := 'CREATE USER YBS' || p_id || ' IDENTIFIED BY YBS' || p_id;
    elsif p_type = 'NC' then
        lv_stmt := 'CREATE USER NC' || p_id || ' IDENTIFIED BY NC' || p_id;
    end if;
    DBMS_OUTPUT.put_line(lv_stmt);
	EXECUTE IMMEDIATE ( lv_stmt );
    lv_stmt := 'INSERT INTO "Nhan_Vien"("Ma_Nhan_Vien", "Ho_Ten", "Phai", "Ngay_Sinh", "CMND", "Que_Quan", "SDT", "CSYT", "Vai_Tro", "Chuyen_Khoa") 
    VALUES(' || p_id || ',''' || p_ho_ten || ''',''' || p_phai || ''', TO_DATE(''' || p_ngay_sinh ||''',''YYYY-MM-DD'')' || ',''' || p_cmnd
    || ''',''' || p_que_quan || ''',''' || p_sdt || ''',' || p_csyt || ',''' || p_vai_tro || ''',''' || p_chuyen_khoa || ''')';
    DBMS_OUTPUT.put_line(lv_stmt);
	EXECUTE IMMEDIATE ( lv_stmt );
end create_nhan_vien;
