create or replace function f_CSYT_HSBA (p_schema varchar2, p_obj varchar2)
return VARCHAR2
IS
    PRAGMA AUTONOMOUS_TRANSACTION;
    usr varchar2(10);
    temp_csyt int := 0;
    id int;
    temp varchar2(30);
BEGIN
    usr := SYS_CONTEXT('USERENV','SESSION_USER');
    if usr not like 'CSYT%' then 
    begin
        temp := '0=1';
        return temp;
    end;
    else begin
        id := REGEXP_REPLACE(upper(usr),'[A-Z]');
        select "Ma_CSYT" into temp_csyt from CSYT 
        where "Ma_CSYT" = id;
        temp := '"Ma_CSYT" = '||temp_csyt;
        return temp;
    end;
    end if;
END;
