        object_schema => 'DBA_CSYT',
        object_name => 'HSBA',
        policy_name => 'p_CSYT_HSBA',
        policy_function => 'f_CSYT_HSBA',
        statement_types => 'INSERT, DELETE',
        update_check => TRUE);
end;
/
