--Create Encrypt-Decrypt Package
CREATE OR REPLACE PACKAGE ENC_DEC 
AS
  FUNCTION ENCRYPT_FUNCTION(p_in IN varchar2,p_key IN VARCHAR2) RETURN VARCHAR2 DETERMINISTIC;
  FUNCTION DECRYPT_FUNCTION(p_in IN VARCHAR2,p_key IN VARCHAR2) RETURN varchar2 DETERMINISTIC;
END ENC_DEC;
/
--SETUP FUNCTION
CREATE OR REPLACE PACKAGE BODY ENC_DEC  IS 
  encryption_type PLS_INTEGER :=
							DBMS_CRYPTO.ENCRYPT_DES
							+DBMS_CRYPTO.CHAIN_CBC
							+DBMS_CRYPTO.PAD_PKCS5;
  FUNCTION ENCRYPT_FUNCTION(p_in IN varchar2,p_key IN VARCHAR2) RETURN VARCHAR2 DETERMINISTIC
  IS
  	encrypted_raw raw(2000);
  BEGIN
       encrypted_raw := sys.dbms_crypto.encrypt(
          src => utl_raw.cast_to_raw(p_in),
          typ => encryption_type,
          key => utl_raw.cast_to_raw(p_key)
      );
      return RAWTOHEX(encrypted_raw);
  END ENCRYPT_FUNCTION;
  FUNCTION DECRYPT_FUNCTION(p_in IN VARCHAR2, p_key IN VARCHAR2) RETURN varchar2 DETERMINISTIC
  IS
    decrypted_raw raw(2000);
  BEGIN 
       decrypted_raw := sys.dbms_crypto.decrypt(
          src => p_in,
          typ => encryption_type,
          key => utl_raw.cast_to_raw(p_key)
      );
      return RAWTOHEX(decrypted_raw);
  END DECRYPT_FUNCTION;
END ENC_DEC ;
